"use strict";function _toConsumableArray(n){if(Array.isArray(n)){for(var a=0,e=Array(n.length);a<n.length;a++)e[a]=n[a];return e}return Array.from(n)}function findMatches(n,a){return a.filter(function(a){var e=new RegExp(n,"gi");return a.name.match(e)||a.company.match(e)})}function displayMatches(){var n=this,a=findMatches(this.value,datas).map(function(a){var e=new RegExp(n.value,"gi");return'\n        <li>\n          <span class="list-item">'+a.name.replace(e,'<span class="hl">'+n.value+"</span>")+",<br>"+a.company.replace(e,'<span class="hl">'+n.value+"</span>")+"</span>\n        </li>\n      "}).join("");list.innerHTML=a}var endpoint="data.json",datas=[];fetch(endpoint).then(function(n){return n.json()}).then(function(n){return datas.push.apply(datas,_toConsumableArray(n))});var searchInput=document.querySelector(".search-field"),list=document.querySelector(".search-list");searchInput.addEventListener("change",displayMatches),searchInput.addEventListener("keyup",displayMatches);